SELECT Et.nometu, An.note, Ep.libepr FROM etudiants AS Et INNER JOIN avoir_note AS An ON Et.numetu=An.numetu INNER JOIN epreuves AS Ep ON An.numepr=Ep.numepr;
SELECT Ma.nommat FROM matieres AS Ma INNER JOIN modules AS Mo ON Mo.nummod=Ma.nummod WHERE Mo.nommod='informatique';
SELECT Ep.*, Ma.nommat, En.nomens FROM epreuves AS Ep INNER JOIN matieres AS Ma ON Ma.nummat=Ep.matepr INNER JOIN enseignants AS En ON En.numens=Ep.ensepr;
SELECT Et.nometu FROM etudiants AS Et INNER JOIN avoir_note AS An ON Et.numetu=An.numetu INNER JOIN epreuves Ep ON Ep.numepr=An.numepr WHERE Ep.libepr='partiel BD' AND An.note=(SELECT An.note FROM avoir_note AS An INNER JOIN epreuves AS Ep ON An.numepr=Ep.numepr INNER JOIN etudiants AS Et ON Et.numetu=An.numetu WHERE Et.nometu='dupont' AND Ep.libepr='partiel BD');
SELECT Et.nometu, Mo.nommod, ROUND(AVG(An.note),2) FROM etudiants AS Et INNER JOIN avoir_note AS An ON Et.numetu=An.numetu INNER JOIN epreuves As Ep ON Ep.numepr=An.numepr INNER JOIN matieres AS Ma ON Ma.nummat=Ep.matepr INNER JOIN modules AS Mo ON Mo.nummod=Ma.nummod GROUP BY Et.nometu,Mo.nommod;
SELECT Et.nometu, Ma.nommat, ROUND(AVG(An.note*Ep.coefepr)/SUM(Ep.coefepr),2) FROM etudiants AS Et INNER JOIN avoir_note AS An ON Et.numetu=An.numetu INNER JOIN epreuves AS Ep ON Ep.numepr=An.numepr INNER JOIN matieres Ma ON Ma.nummat=Ep.matepr INNER JOIN modules Mo ON Mo.nummod=Ma.nummod GROUP BY Ma.nommat, Et.nometu ORDER BY Et.nometu;
SELECT Ep.libepr, Eg.nomens, MAX(An.note) FROM etudiants AS Et INNER JOIN avoir_note AS An ON An.numetu=Et.numetu INNER JOIN epreuves AS Ep ON Ep.numepr=An.numepr INNER JOIN enseignants AS Eg ON Eg.numens=Ep.ensepr GROUP BY Ep.libepr, An.note, Eg.nomens ORDER BY Ep.libepr;